import { useState, useEffect } from 'react';
import styles from './PeoplePage.module.css';
import { getApiResource } from '../../utils/network';
import { API_PEOPLE, SWAPI_PEOPLE } from '../../constants/api';
import PeopleList from '../../components/PeoplePage/PeopleList';
import { getPeopleId, getPeopleImage } from '../../services/getPeopleData';

const PeoplePage = () => {
    const [people, setPeople] = useState(null);
    const getResource = async (url) => {
        // console.log(await getApiResource(url));
        const data = await getApiResource(url);
        const peopleList = data.results.map(({name, url}) => {
            // console.log(name, url);
            // const id = getPeopleId(url);
            const id = getPeopleId(url, SWAPI_PEOPLE);
            const img = getPeopleImage(id);
            // console.log(img);
            // console.log('id ', id);
            return {
                id,
                name,
                img,
            }
        });
        // console.log({people});
        setPeople(peopleList);
        // console.log({people});
    };
    useEffect(()=> {
        getResource(API_PEOPLE);
    },[]);
    return (
        <>PeoplePage
            { people && <PeopleList people = {people}/> }
        </>
    );
    // <li key={(idex+1).toString()}>{url}</li>

    // {people && people.map(({name, url}, idex)=> 
    //      <div>
    //      <li key={idex.toString()}>{name +' '+idex}</li>
    //      <li key={(idex+1).toString()}>{url}</li>
    //      </div>
    // )};
}

export default PeoplePage;
