import {useState, useEffect} from 'react';
import PropTypes from 'prop-types';
import {changeHTTP, makeConcurrentRequest} from '../../../utils/network';
import styles from './PersonFilms.module.css';

const PersonFilms = ({films}) => {
    const [filmsName, setFilmsName] = useState([]);
    // const responses = films.map(urlHttp => {
    //     const urlHttps = changeHTTP(urlHttp);
    //     return fetch(urlHttps);
    // });
    // console.log('films responses', responses);
    // const res = Promise.all(responses.map(responses => responses.json()))
    // .then(resp => resp.map(r => console.log(r.json().value)))

    // console.log('res', res);
    useEffect(() => {
        (async () => {
            const filmsHTTPS = films.map(url => changeHTTP(url));
            const response = await makeConcurrentRequest(filmsHTTPS);
            setFilmsName(response);
            let respFilter = response.map(film => {  console.log(film.title); return film.title })
            // console.log('response PersonFilms', response);
            console.log('respFilter', respFilter);
        })();
    }, []);
    return (
        <>
            PersonFilms:
            {filmsName.length && filmsName.map((film, index) => <div key={index} style={{color: 'white',}}>${film.title}</div>)}
            {films.map(film => <span>{film}</span>)}
        </>
    );
}

PersonFilms.propTypes = {
    films: PropTypes.array,
}

export default PersonFilms;
