import {useState} from 'react';

const withErrorApi = View => {
    return props => {
        const [errorApi, setErrorApi] = useState(false);
        return (
            <> 
                { errorApi ? <h1>Error</h1> : 
                    <View setErrorApi = {setErrorApi} {...props}/> }
            </>
        )
    }
}

export {withErrorApi};
